// src/components/Layout.js
import React from 'react';
import { Link } from 'react-router-dom'; 
import logo from '../images/LOGO.png'; 

function Layout({ children }) {
  return (
    <div>
      <header style={headerStyle}>
        <div style={logoContainerStyle}>
          <img src={logo} alt="Logo" style={logoStyle} />
        </div>
        <nav style={navStyle}>
          <Link to="/" style={linkStyle}>Accueil</Link>
          <Link to="/about" style={linkStyle}>A Propos</Link>
        </nav>
      </header>
      <main>{children}</main>
      <footer style={footerStyle}>
        <p>&copy; 2020 Kasa. All rights reserved</p>
      </footer>
    </div>
  );
}

// Styles en ligne ou importez un fichier CSS/Sass pour les styles
const headerStyle = {
  display: 'flex',
  justifyContent: 'space-between',
  alignItems: 'center',
  padding: '0 20px',
  backgroundColor: '#333',
  height: '60px',
};

const logoContainerStyle = {
  display: 'flex',
  alignItems: 'center',
};

const logoStyle = {
  height: '40px', // Ajustez la taille du logo selon vos besoins
};

const navStyle = {
  display: 'flex',
  gap: '15px',
};

const linkStyle = {
  color: '#fff',
  textDecoration: 'none',
  fontSize: '16px',
};

const footerStyle = {
  textAlign: 'center',
  padding: '10px',
  backgroundColor: '#333',
  color: '#fff',
};

export default Layout;
