import React from 'react';
import './Card.scss'; // Importer le fichier Sass spécifique à Card

function Card({ title, description, imageUrl, link }) {
  return (
    <div className="card">
      <img src={imageUrl} alt={title} className="card-image" />
      <div className="card-content">
        <h3>{title}</h3>
        <p>{description}</p>
        {link && <a href={link} className="card-link">Learn More</a>}
      </div>
    </div>
  );
}

export default Card;
