import React from 'react';
import './Card.scss';
function Card({ title,imageUrl, link }) {
  return (
    <div className="card">
      <img src={imageUrl} alt={title} className="card-image" />
      <div className="card-content">
        <h3>{title}</h3>
        {link && <a href={link} className="card-link"></a>}
      </div>
    </div>
  );
}

export default Card;
