import React from 'react';
import { useParams } from 'react-router-dom';
import data from '../data/logements.json';
import Collapse from '../components/collapse';
import './LogementPage.scss';

const LogementPage = () => {
  const { id } = useParams();
  const logement = data.find((item) => item.id === id);

  if (!logement) {
    return <p>Logement non trouv√©</p>;
  }

  return (
    <div>
      <img src={logement.cover} alt={logement.title} className="cover" />
      
      <div className="titre">
        <h1>{logement.title}</h1>
        <div className="host">
          <p>{logement.host.name}</p>
          <img src={logement.host.picture} alt={logement.host.name} className="image-host" />
        </div>
      </div>

      <h2>{logement.location}</h2>      

      <div className="tags">
        {logement.tags.map((tag, index) => (
          <span key={index} className="tag">{tag}</span>
        ))}
      </div>

      <div className="collapse-group">
        <Collapse title="Description">
          <p>{logement.description}</p>
        </Collapse>
        <Collapse title="Equipements">
          
            {logement.equipments.map((equipment, index) => (
              <ul key={index}>{equipment}</ul>
            ))}
        
        </Collapse>
      </div>
    </div>
  );
};

export default LogementPage;
