// src/components/Banner.js

import React from 'react';
import './Banner.scss';

function Banner({ title, backgroundImage }) {
  const bannerStyle = {
    backgroundImage: `url(${backgroundImage})`,
  };

  return (
    <div className="banner" style={bannerStyle}>
      <h1>{title}</h1>
    </div>
  );
}

export default Banner;
